import "./styles.css";

export default function App() {
  const nameToDisplay = 'James';

  return (
    <div className="App">
      <Greeting name={nameToDisplay} />
    </div>
  );
}
function Greeting({ name }) {

  const coaches = ['James', 'John', 'Jordan'];


  if (coaches.includes(name)) {
    return <p>Hello, Coach!</p>;
  } else {
    return <p>Hello, {name}!</p>;
  }
}
