import "./styles.css";

function Greeting({ name }) {
  // Define an array of coach names
  const coaches = ['Coach1', 'Coach2', 'Coach3']; // Replace with your coach names

  // Check if the provided name is in the coaches array
  if (coaches.includes(name)) {
    return <p>Hello, Coach!</p>;
  } else {
    return <p>Hello, {name}!</p>;
  }
}

function App() {
  const nameToDisplay = 'Anton';

  return (
    <div className="App">
      <Greeting name={nameToDisplay} />
    </div>
  );
}

export default App;
